import { useSelector,useDispatch } from "react-redux"
import { addCart, removeCart } from "../redux/Slices/cartSlice";
import toast from "react-hot-toast"
const Product = ({ post }) => {
  // const [selected, setSelected] = useState(false);
  
  // console.log(useSelector((state) => state.cart))
  const {cart} = useSelector((state) => state);
  const dispatch = useDispatch();

  const addCartItem = () =>{
    dispatch(addCart(post));
    toast.success("Item added Successfully");
  }

  const removeCartItem = ()=>{
    dispatch(removeCart(post.id));
    toast.error("Item removed Successfully");
    console.log(cart)
  }

  return (
    <div className="outline flex flex-col justify-between items-center">

          <p className="font-bold text-gray-700 mt-2 text-left text-lg w-40 truncate">{post.title}</p>
          <p className="w-40 font-normal text-sm">{post.description.split(" ").slice(0,10).join(" ") + "..."}</p>

      <img src={post.image} className="h-[50%]" />

      <div>
        <p>{post.price}</p>
        {
          cart.some((p) => p.id == post.id) ? <button onClick={removeCartItem}>Remove Item</button>
          : <button onClick={addCartItem}>Add To Cart</button>
        }
      </div>
    </div>
  );
};

export default Product;
