import { useSelector,useDispatch } from "react-redux"
import { addCart, removeCart } from "../redux/Slices/cartSlice";
import toast from "react-hot-toast"
const Product = ({ post }) => {
  // const [selected, setSelected] = useState(false);

  const {cart} = useSelector((state) => state);
  console.log(cart)
  const dispatch = useDispatch();

  const addCartItem = () =>{
    dispatch(addCart(post));
    toast.success("Item added Successfully");
  }

  const removeCartItem = ()=>{
    dispatch(removeCart(post.id));
    toast.error("Item removed Successfully");
  }

  return (
    <div>
      <div>
        <p className="font-bold">{post.title}</p>
        <p>{post.description}</p>
      </div>

      <img src={post.image} width={200} />

      <div>
        <p>{post.price}</p>
        {/* {
          cart.some((p) => p.id == post.id) ? <button onClick={removeCartItem}>Remove Item</button>
          : <button onClick={addCartItem}>Add To Cart</button>
        } */}
      </div>
    </div>
  );
};

export default Product;
