import React from "react";
import { useSelector , useDispatch } from "react-redux";

// ab redux store ko consume krna hai 

export const Counter = () => {
  const dispatch = useDispatch();
  const count  = useSelector((state) => state.Counter.value);
  // const count  = 0; 
  console.log(count);
  return (
    <div>
      <h2>Increment</h2>
      <p>{count}</p>
      <h1>Decrement</h1>
    </div>
  );
};
